svg.script:
let svgs = document.children[0];
let paths = Array.from(svgs.children).filter(function(c) {
    var orig = c.style.stroke;
    c.onmouseover = function() {
        c.style.stroke = "rgb(0, 0, 255)";
    };
    c.onmouseout = function () {
        c.style.stroke = orig;
    };
    c.onclick = function () {
        svgs.removeChild();
    }
});

for (let path of paths) {
    console.log(path);
}
